package Lect_5_Basics_Data_Type_And_Loop;

public class Type_Casting1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=90;
		x+=1;//x=(int)(x+1);
		byte b=9;
		b+=1;// b=(byte)(b+1);
		b=(byte)(b+1);
		b++;///b=(byte)(b+1)
		long l=87654389989l;// explicit

	}

}
